/* eslint-disable no-undef */
import React from 'react';
import PropTypes from 'prop-types';
import style from  './ListItem.module.scss';

const ListItem = ({
    image,
    name,
    description,
    twitterLink
}) => (
    <li className="listItem__wrapper">
        <img 
        className="listItem__image" 
        src={image} 
        alt={name} 
        />
        <div>
        <h2 className="listItem__name">
        {name}
        </h2>
        <p className="listItem__description">
        {description}
        </p>
        <a href={twitterLink} className="listItem__button" target="_blank" rel="noopener noreferrer">
            visit Twitter page
            </a>
        </div>
    </li>
);

// rel w linku jest dla bezpieczeństwa

ListItem.propTypes = {
    image: PropTypes.string,
    // inne to number, func (czyli funkcja), arrayOf ... 
    name: PropTypes.string.isRequired,
    description: PropTypes.string,
    twitterLink: PropTypes.string.isRequired,
};

ListItem.defaultProps = {
    description: 'One of the React creators',
    image: null,
}

// description nie jest wymagane, a defaultowe mozna nadac w ten sposob
export default ListItem;
